exports.getCookies = function (req) {
    var cookies = {};
    var parts = req.headers ['cookie'];

    if (parts) {
        parts = parts.split (';');
        parts.forEach (function (p) {
                    p = p.split ('=');
                    cookies[p[0].trim()] = p[1];
                });
    }

    return cookies;
}

exports.setCookies = function (res, cookies) {
    var cookiehdr = [];
    for (var k in cookies) cookiehdr.push ([k + '=' + cookies[k] + ';']);
    res.setHeader ('Set-Cookie', cookiehdr);
}


